
public class SumOfArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] myArray = {1, 2, 3, 4, 5};
		int sum = 0;

		for (int i = 0; i < myArray.length; i++) {
		    sum += myArray[i];
		}

		System.out.println("The sum of the elements is: " + sum);

	}

}
